    /************
     * Offers slider
     */
    $(function () {
        slider();
        $("#offers .slider-nav ul li a").click(function() {
            slider();
        });
    });



    function slider() {
        var size = $(".active-category .carousel > div").length; //number of items in carousel
        var position = 0; // current position of carousal
        var sliderIntervalID; // used to clear autoplay
        var sliderInterval = 10000; // interval of autoplay
        var changeInterval = 1000; // interval between change of slides

        $(".active-category .carousel  > div").not(".show").fadeOut(); //fadeout all items except the one with .show

        startSlider(); //starts autoplay

        // function to start auto play
        function startSlider() {
            sliderIntervalID = setInterval(function() {
                run(1);
            }, sliderInterval);
        }

        // on mouseover stop the autoplay
        $(".active-category .carousel ").mouseover(function() {
            clearInterval(sliderIntervalID);
        });
        // on mouseout starts the autoplay
        $(".active-category .carousel ").mouseout(function() {
            startSlider();
        });

        //on right clicking the carousal
        $(".active-category .carousel  > #right").click(function() {
            run(1);
        });


        //on left button click of the carousal
        $(".active-category .carousel  > #left").click(function() {
            run(0);
        });


        //moves the slider back or forth based on x (1 = forward 0 = backward)
        function run(x) {
            position = $(".active-category .carousel ").find(".show").index();
            if (x == 1)
                ++position;
            else
                --position;
            checker();
            changeCarousal(); //function call that actually changes slides
        }


        //just a function to reposition the slider when out of bounds
        function checker() {
            if (position >= size) {
                position = 0;
            } else if (position < 0) {
                position = size - 1;
            }
            //console.log(position);
        };


        //this changes the image and button selection
        function changeCarousal() {
            $(".active-category .carousel ").find(".show").removeClass("show").fadeOut();
            $(".active-category .carousel  > div").eq(position).fadeIn(changeInterval).addClass("show");
            $(".active-category .carousel  > ul").find(".showli").removeClass("showli");
            $(".active-category .carousel  > ul > li").eq(position).addClass("showli");
        }

        //when use clicks slider button
        $(".active-category .carousel  > ul > li").click(function() {
            console.log('clicking');
            position = $(this).index();
            var temp = $(".active-category .carousel ").find(".show").index();
            if (position != temp) {
                changeCarousal();
            }
        });

    }

